UPDATE personne SET Personne_Nom='" + TB_Fam_Name.Text.Trim() + "',Personne_Prenom='" + TB_Name.Text.Trim() + "',Personne_DateNaiss='" + DTP_Naissance.Value.Date.ToString("yyyy-MM-dd").Trim() + "',Personne_Mail='" + TB_Mail.Text.Trim() + "', Personne_Fax='" + TB_Fax_Number.Text.Trim() + "',Personne_Adresse='" + TB_Address.Text.Trim() + "',Personne_Metier='" + TB_Job.Text.Trim() + "',Personne_Telephone='" + TB_Phone_Number.Text.Trim() + "',Personne_Password='" + TB_Password.Text.Trim() + "',Personne_SiteWeb='" + TB_Website.Text.Trim() + "' WHERE Personne_Mail='" + Tofind.Trim() + "';